const blacklist = ['plug-menu'];
const blacklistWithFullPath = blacklist.map(module => `./${module}/index.js`);
const ctx = require.context('./', true, /^\.\/plug-.*\/index.js$/);
const modules = ctx.keys()
    .filter(path => !blacklistWithFullPath.includes(path))
    .map(ctx).map(module => module.default);

export default modules;