import electron from 'electron';

export default {
    getCursorScreenPoint: (...args) => electron.screen.getCursorScreenPoint(...args),
    getPrimaryDisplay: (...args) => electron.screen.getPrimaryDisplay(...args),
    getAllDisplays: (...args) => electron.screen.getAllDisplays(...args),
    getDisplayNearestPoint: (...args) => electron.screen.getDisplayNearestPoint(...args),
    getDisplayMatching: (...args) => electron.screen.getDisplayMatching(...args),
};