import electron from 'electron';
import 'plug';

plug.emit(`plug.network.${navigator.onLine ? 'online' : 'offline'}`);
window.addEventListener('online',  () => plug.emit('plug.network.online'));
window.addEventListener('offline',  () => plug.emit('plug.network.offline'));
electron.ipcRenderer.on('plug.notify', (event, options) => new Notification(options.title, options));
